INSERT INTO `holes` VALUES ('976', 'Rockwell Automation Allen-Bradley PowerMonitor 1000访问控制错误漏洞', 'https://www.cnvd.org.cn/flaw/show/CNVD-2018-26886', 'CNVD-2018-26886', '2018-12-28', '高', 'Rockwell Automation Allen-Bradley PowerMonitor 1000 无', 'CVE-2018-19616', 'Rockwell Automation Allen-Bradley PowerMonitor 1000是美国罗克韦尔（Rockwell Automation）公司的一款电源监控设备。Rockwell Automation Allen-Bradley PowerMonitor 1000的Web页面中存在访问控制错误漏洞，攻击者可利用该漏洞添加具有管理员权限的新用户。', '通用型漏洞', 'http://packetstormsecurity.com/files/150619/Rockwell-Automation-Allen-Bradley-PowerMonitor-1000-Authentication-Bypass.html', '厂商尚未提供漏洞修复方案，请关注厂商主页更新：https://www.rockwellautomation.com/', '(无补丁信息)', '(暂无验证信息)', '2018-12-07', '2018-12-28', '2018-12-28');
