INSERT INTO `holes` VALUES ('1130', 'ABB Panel Builder 800输入验证漏洞', 'https://www.cnvd.org.cn/flaw/show/CNVD-2018-18146', 'CNVD-2018-18146', '2018-09-10', '中', 'ABB Panel Builder 800', 'CVE-2018-10616', 'ABB Panel Builder 800是瑞士ABB公司的一套基于Web的HMI（人机界面）系统。ABB Panel Builder 800（所有版本）中存在输入验证漏洞，漏洞源于程序未能正确的过滤用户提交的输入，攻击者可利用该漏洞在使用了受影响产品的计算机上注入并运行任意代码。', '通用型漏洞', 'http://search-ext.abb.com/library/Download.aspx?DocumentID=3BSE092089&amp;Action=Launch\n', '厂商尚未提供漏洞修复方案，请关注厂商主页更新：https://new.abb.com/', '(无补丁信息)', '(暂无验证信息)', '2018-07-20', '2018-09-10', '2018-09-10');
