INSERT INTO `holes` VALUES ('1176', 'SIEMENS SCALANCE M875信息泄露漏洞', 'https://www.cnvd.org.cn/flaw/show/CNVD-2018-11394', 'CNVD-2018-11394', '2018-06-13', '中', 'SIEMENS SCALANCE M875', 'CVE-2018-11449', 'SCALANCE M工业路由器用于通过移动网络对工厂进行安全的远程访问，例如， GPRS或UMTS，具有防火墙的集成安全功能以防止未经授权的访问和VPN来保护数据传输。SIEMENS SCALANCE M875存在信息泄露漏洞。允许攻击者获取管理密码。', '通用型漏洞', 'https://cert-portal.siemens.com/productcert/pdf/ssa-977428.pdf', '用户可参考如下供应商提供的安全公告获得补丁信息：https://cert-portal.siemens.com/productcert/pdf/ssa-977428.pdf', 'SIEMENS SCALANCE M875信息泄露漏洞的补丁', '(暂无验证信息)', '2018-06-13', '2018-06-13', '2018-06-13');
